from collections import Counter

line = ""


def part_1_and_2(size) -> int:
    buffer = Counter(line[:size])
    for pos, (old_char, new_char) in enumerate(zip(line, line[size:])):
        if buffer.most_common(1)[0][1] == 1:
            return pos + size
        buffer[old_char] -= 1
        buffer[new_char] += 1

if __name__ == '__main__':
    print(part_1_and_2(14))
